<?php class Fire_API{public static $API_VERSION="1.1";private $API_UPDATE_PAGE="https://api.fire-softwares.ga/?page=download";function updatesCheck(){$updater=new API;try{if($updater->getServerInformation(ServerName::$FIRE_SOFTWARES,ServerInfoType::$ONLINE)===TRUE){$lastversion=$updater->getServerInformation(ServerName::$API_STATUS,ServerInfoType::$VERSION);if(doubleval($lastversion)>doubleval($API_VERSION))$this->console_log(Messages::$API_WARNING_PREFIX." ".str_replace("%api-update-page%",$API_UPDATE_PAGE,str_replace("%new-version%",$lastversion,Messages::$API_UPDATE_AVAILABLE)),'warn');elseif(doubleval($lastversion)<doubleval($API_VERSION))$this->console_log(Messages::$API_WARNING_PREFIX." ".str_replace("%api-update-page%",$API_UPDATE_PAGE,str_replace("%lastest-stable%",$lastversion,str_replace("%api-version%",$API_VERSION,Messages::$API_BETA))),'warn');else $this->console_log(Messages::$API_PREFIX." ".str_replace("%api-version%",$API_VERSION,Messages::$API_UP_TO_DATE));}}catch(Exception $e){$this->console_log(Messages::$API_ERROR_PREFIX." ".Messages::$UPDATE_CHECK_EXCEPTION,'error');$this->console_log(Messages::$API_ERROR_PREFIX." ".$e->getMessage(),'error');}$this->console_log("\n\r".Messages::$API_PREFIX." ".str_replace("%api-version%",$API_VERSION,Messages::$API_HELLO));}function __construct(){$this->updatesCheck();}function console_log($output,$command='log',$with_script_tags=true){$js_code='console.'.$command.'('.json_encode($output,JSON_HEX_TAG).');';if($with_script_tags)$js_code='<script>'.$js_code.'</script>';echo $js_code;}}class Messages{public static $SLIDER_CAPTIONS_AND_IMAGES_ARRAYS_NOT_SAME_LENGTH_EXCEPTION="Captions and images arrays need to be same length.";public static $UPDATE_CHECK_EXCEPTION="Can't check for updates :";public static $API_PREFIX="[Fire-API]";public static $API_ERROR_PREFIX="[Fire-API Error]";public static $API_WARNING_PREFIX="[Fire-API Warning]";public static $API_HELLO="Fire-API version %api-version% by Fire-Softwares successf