yle="'.$style.'" ':'').'class="tooltip">'.$htmlCode.'<span '.($id!=""?'id="'.$id.'-tooltip-content" ':'').'class="'.$formatted_color_scheme_classes.' tooltip-content">'.$content.'</span></div>';}}class WebFrameworkComponentInteraction{function toggleDropdown($dropdownId){echo "<script>dropdown('".$dropdownId."');</script>";}function openModal($modalId){echo "<script>openModal('".$modalId."');</script>";}function closeModal($modalId){echo "<script>closeModal('".$modalId."');</script>";}function echoProgressValue($progressId){echo "<script>getProgressValue('".$progressId."');</script>";}function setProgressValue($progressId,$value){echo "<script>setProgressValue('".$progressId."', ".$value.");</script>";}function plusSlides($n){echo "<script>plusSlides(".$n.");</script>";}function currentSlide($n){echo "<script>currentSlide(".$n.");</script>";}function domSyntaxicColoration($id,$autowidth=true,$mode="html"){echo "<script>domSyntaxicColoration('".$id."', ".$autowidth.", ".$mode.");</script>";}} ?>
